package com.rmit.trading_backend.product.repository;import com.rmit.trading_backend.product.model.Product;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.repository.query.Param;import java.util.List;import java.util.Optional;public interface ProductRepository extends JpaRepository<Product, Integer> {    Optional<Product> findProductByNameContaining(String name);    List<Product> findProductsByBrandContaining(String brand);    List<Product> findAllProductByCategoryContaining(@Param("name") String categoryName);}