package com.rmit.trading_backend.repository;import com.rmit.trading_backend.model.product.Product;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.repository.query.Param;import java.util.List;public interface ProductRepository extends JpaRepository<Product, Long> {    List<Product> findProductByNameContaining(String name);    List<Product> findProductsByBrandContaining(String brand);    List<Product> findAllProductByCategoryId(@Param("id") int categoryID);    List<Product> findAllProductByCategoryName(@Param("name") String categoryName);}